mapper.insert:
INSERT INTO forum_comment (
    post_id, parent_id, content, user_id, status, create_time
) VALUES (
             #{postId}, #{parentId}, #{content}, #{userId}, '0', sysdate()
         )

mapper.update:
UPDATE forum_comment
content = #{content}, update_time = sysdate()
WHERE comment_id = #{commentId}

UPDATE forum_comment
SET status = '1'
WHERE comment_id = #{commentId}
