mapper.insert:
INSERT INTO forum_post (
    title, content, user_id, status, create_time
) VALUES (
             #{title}, #{content}, #{userId}, '0', sysdate()
         )

mapper.update:
UPDATE forum_post
title = #{title}, content = #{content}, update_time = sysdate()
WHERE post_id = #{postId}

UPDATE forum_post
SET status = '1'
WHERE post_id = #{postId}

UPDATE forum_post
SET comment_count = comment_count + #{count}
WHERE post_id = #{postId}
